select dept_unique_id

	, sum(online_duration) online_duration


	, sum(talk_count) talk_count


	, sum(individual_talk_count) individual_talk_count


	, sum(group_talk_count) group_talk_count


	, sum(talk_duration) talk_duration


	, sum(individual_talk_duration) individual_talk_duration


	, sum(group_talk_duration) group_talk_duration


	, sum(video_all_count) video_all_count


	, sum(video_call_count) video_call_count


	, sum(video_upload_count) video_upload_count


	, sum(video_rollcall_count) video_rollcall_count


	, sum(video_conf_count) video_conf_count


	, sum(video_duration) video_duration


	, sum(sos_count) sos_count


	, sum(photo_upload_count) photo_upload_count


	, sum(im_count) im_count


	, sum(individual_im_count) individual_im_count


	, sum(group_im_count) group_im_count


	, sum(onpost_duration) onpost_duration


	, sum(mileage) mileage


	, sum(tmp_group_count) tmp_group_count


	, sum(tmp_group_file_count) tmp_group_file_count

from sta_day_dept


		and dept_unique_id in

			#{id}





				and time_ymd >= (#{start_time} * 100)


				and time_ymd <= (#{end_time} * 100 + 31)




				and time_ymd >= #{start_time}


				and time_ymd <= #{end_time}







		and

			online_duration >= ${min}


			talk_count >= ${min}


			individual_talk_count >= ${min}


			group_talk_count >= ${min}


			talk_duration >= ${min}


			individual_talk_duration >= ${min}


			group_talk_duration >= ${min}


			video_all_count >= ${min}


			video_call_count >= ${min}


			video_upload_count >= ${min}


			video_rollcall_count >= ${min}


			video_conf_count >= ${min}


			video_duration >= ${min}


			sos_count >= ${min}


			photo_upload_count >= ${min}


			im_count >= ${min}


			individual_im_count >= ${min}


			group_im_count >= ${min}


			onpost_duration >= ${min}


			mileage >= ${min}


			tmp_group_count >= ${min}


			tmp_group_file_count >= ${min}



		and

			online_duration <= ${max}


			talk_count <= ${max}


			individual_talk_count <= ${max}


			group_talk_count <= ${max}


			talk_duration <= ${max}


			individual_talk_duration <= ${max}


			group_talk_duration <= ${max}


			video_all_count <= ${max}


			video_call_count <= ${max}


			video_upload_count <= ${max}


			video_rollcall_count <= ${max}


			video_conf_count <= ${max}


			video_duration <= ${max}


			sos_count <= ${max}


			photo_upload_count <= ${max}


			im_count <= ${max}


			individual_im_count <= ${max}


			group_im_count <= ${max}


			onpost_duration <= ${max}


			mileage <= ${max}


			tmp_group_count <= ${max}


			tmp_group_file_count <= ${max}



group by dept_unique_id
limit ${start}, ${count}

