select id, corpid, time_ymd, dept_unique_id, dept_name, online_duration, logon_time, talk_count, individual_talk_count, group_talk_count, talk_duration, individual_talk_duration, group_talk_duration,
video_all_count, video_call_count, video_upload_count, video_rollcall_count, video_conf_count, video_duration, sos_count, photo_upload_count, im_count, individual_im_count, group_im_count, onpost_duration,
mileage, tmp_group_count, tmp_group_file_count, state, update_time
from sta_day_dept


		corpid = #{corpid}


		time_ymd = #{timeYmd}


		dept_unique_id = #{deptUniqueId}

select id, corpid, time_ymd, dept_unique_id, dept_name, online_duration, logon_time, talk_count, individual_talk_count, group_talk_count, talk_duration, individual_talk_duration, group_talk_duration,
video_all_count, video_call_count, video_upload_count, video_rollcall_count, video_conf_count, video_duration, sos_count, photo_upload_count, im_count, individual_im_count, group_im_count, onpost_duration,
mileage, tmp_group_count, tmp_group_file_count, state, update_time
from sta_day_dept
where dept_unique_id = #{uniqueId}

	and time_ymd between #{startTime} and #{endTime}

select dept_unique_id, dept_name, sum(online_duration) online_duration, sum(talk_count) talk_count, sum(individual_talk_count) individual_talk_count, sum(group_talk_count) group_talk_count, sum(talk_duration) talk_duration, sum(individual_talk_duration) individual_talk_duration, sum(group_talk_duration) group_talk_duration, sum(video_all_count) video_all_count, sum(video_call_count) video_call_count, sum(video_upload_count) video_upload_count, sum(video_rollcall_count) video_rollcall_count, sum(video_conf_count) video_conf_count, sum(video_duration) video_duration, sum(sos_count) sos_count, sum(photo_upload_count) photo_upload_count, sum(im_count) im_count, sum(individual_im_count) individual_im_count, sum(group_im_count) group_im_count, sum(onpost_duration) onpost_duration, sum(mileage) mileage, sum(tmp_group_count) tmp_group_count, sum(tmp_group_file_count) tmp_group_file_count from ( select * from sta_day_dept where time_ymd like CONCAT(#{date}, '%')
and dept_unique_id in (${uniqueIdIn})
) as temp_1 group by dept_unique_id, dept_name

select sum(online_duration) online_duration, sum(talk_count) talk_count, sum(individual_talk_count) individual_talk_count, sum(group_talk_count) group_talk_count,
