from (
		 select * from sta_day_dept where time_ymd like CONCAT(#{date}, '%') and dept_unique_id = #{uniqueId}
	 ) as temp_1

select dept_unique_id, time_ymd, sum(online_duration) online_duration, sum(talk_count) talk_count, sum(individual_talk_count) individual_talk_count, sum(group_talk_count) group_talk_count, sum(talk_duration) talk_duration, sum(individual_talk_duration) individual_talk_duration, sum(group_talk_duration) group_talk_duration, sum(video_all_count) video_all_count, sum(video_call_count) video_call_count, sum(video_upload_count) video_upload_count, sum(video_rollcall_count) video_rollcall_count, sum(video_conf_count) video_conf_count, sum(video_duration) video_duration, sum(sos_count) sos_count, sum(photo_upload_count) photo_upload_count, sum(im_count) im_count, sum(individual_im_count) individual_im_count, sum(group_im_count) group_im_count, sum(onpost_duration) onpost_duration, sum(mileage) mileage, sum(tmp_group_count) tmp_group_count, sum(tmp_group_file_count) tmp_group_file_count from sta_day_dept
dept_unique_id in
#{uniqueId}
and time_ymd in
#{date}
