select dept_unique_id, sum(1) talk_count from sta_user_logon_record_origin


		`real` = 1


		and dept_unique_id in

			#{id}





				and time_ymd >= (#{start_time} * 100)


				and time_ymd <= (#{end_time} * 100 + 31)




				and time_ymd >= #{start_time}


				and time_ymd <= #{end_time}







group by dept_unique_id
limit ${start}, ${count}

select id, corpid, time_ymd, dept_unique_id, dept_name, online_duration, logon_time, talk_count, individual_talk_count, group_talk_count, talk_duration, individual_talk_duration,
group_talk_duration, video_all_count, video_call_count, video_upload_count, video_rollcall_count, video_conf_count, video_duration, sos_count, photo_upload_count, im_count,
individual_im_count, group_im_count, onpost_duration, mileage, tmp_group_count, tmp_group_file_count, state, update_time from sta_day_dept


		dept_unique_id = #{uniqueId}


		and time_ymd >= #{startTime}


		and time_ymd <= #{endTime}

select dept_unique_id, sum(talk_count + video_all_count + sos_count + photo_upload_count + im_count + individual_im_count + group_im_count) talk_count from sta_day_dept
dept_unique_id in
#{uniqueId}
and time_ymd in
#{date}
group by dept_unique_id order by talk_count DESC
